import React, { useState, useRef } from 'react';
import TodoForm from './TodoForm';
import Todo from './Todo';
import { Link } from 'react-router-dom';
import { TiArrowBack } from 'react-icons/ti';


function Home() {
    const [todos, setTodos] = useState([]);
    const todoRef = useRef(null);



    const addTodo = todo => {
        if (!todo.text || /^\s*$/.test(todo.text)) {
            return;
        }

        const newTodos = [todo, ...todos];

        setTodos(newTodos);
        // console.log(todoRef.current.);
        console.log(todos[1]);
        document.getElementById('total').innerHTML = todos.length + 1;
    };

    const updateTodo = (todoId, newValue) => {
        if (!newValue.text || /^\s*$/.test(newValue.text)) {
            return;
        }

        setTodos(prev => prev.map(item => (item.id === todoId ? newValue : item)));
    };

    const removeTodo = id => {
        const removedArr = [...todos].filter(todo => todo.id !== id);

        var total = document.getElementById('total').innerHTML;
        total -= 1;
        document.getElementById('total').innerHTML = total;

        var completed = Number(document.getElementById('completed').innerHTML);

        document.getElementById('completed').innerHTML = completed - 1;

        console.log('completed: ' + completed);

        var total = document.getElementById('total').innerHTML;
        var pending = total - completed;
        console.log('pending: ' + pending);
        document.getElementById('pending').innerHTML = pending;

        console.log('total: ' + total);
        setTodos(removedArr);
    };

    const completeTodo = id => {
        let updatedTodos = todos.map(todo => {
            if (todo.id === id) {
                todo.isComplete = !todo.isComplete;
                if (todo.isComplete) {

                    var completed = Number(document.getElementById('completed').innerHTML) + 1;

                    document.getElementById('completed').innerHTML = completed;

                    console.log('completed: ' + completed);

                    var total = document.getElementById('total').innerHTML;
                    var pending = total - completed;
                    console.log('pending: ' + pending);
                    document.getElementById('pending').innerHTML = pending;
                }
                else {
                    var completed = Number(document.getElementById('completed').innerHTML) - 1;

                    document.getElementById('completed').innerHTML = completed;

                    console.log('completed: ' + completed);


                    var total = document.getElementById('total').innerHTML;
                    var pending = total - completed;
                    console.log('pending: ' + pending);
                    document.getElementById('pending').innerHTML = pending;
                }
            }
            return todo;
        });

        setTodos(updatedTodos);
    };

    return (
        <>
            <div className='icons'>
                <Link to="/login"><TiArrowBack className='logout-icon' /></Link>
            </div>

            <div className="row d-flex justify-content-center pt-4 pb-4">
                <div className="col-lg-6 col-md-6">

                    <div className="card" >
                        <div className="card-header">
                            <h3 className="text-center mb-4">What's the Plan for Today?</h3>

                            <TodoForm onSubmit={addTodo} />
                        </div>
                        <div className="card-body scroller">
                            <Todo
                                todos={todos}
                                completeTodo={completeTodo}
                                removeTodo={removeTodo}
                                updateTodo={updateTodo}
                            />
                        </div>
                        <div className="card-footer">

                            <div className='row justify-content-center'>
                                <div className='col'>
                                    <span>Completed: <small id='completed' className="text-muted" ref={todoRef}>0</small></span>
                                </div>
                                <div className='col'>
                                    <span>Pending: <small id='pending' className="text-muted" >0</small></span>
                                </div>
                                <div className='col'>
                                    <span>Total: </span> <small id='total' className="text-muted" >0</small>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </>
    );
}

export default Home;
